import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { InventoryService } from 'src/app/services/inventory.service';
import { Observable, Observer } from 'rxjs';
import { AuthService } from 'src/app/services/auth/auth.service';
import { CommonModule } from '@angular/common';
import { AuthorizeService } from '../../../api-authorization/authorize.service';

@Component({
  selector: 'app-item-page',
  templateUrl: './item-page.component.html',
  styleUrls: ['./item-page.component.css']
})

export class ItemPageComponent implements OnInit {
  item: any;
  public isAuthenticated?: Observable<boolean>;

  constructor(
    private route: ActivatedRoute,
    private inventoryService: InventoryService,
    // private authService: AuthService, 
    private router: Router,
    private authorizeService: AuthorizeService) { }

  // isUserLoggedIn(): boolean {

  //   const user = this.authService.isAuthenticated();
  //   // console.log('User: ', user);
  //   return user !== null;

  // }

  buyItem(item: any): void {
    if (this.isAuthenticated) {
      console.log('logged');
    } else {
      console.log('not logged');
      this.router.navigate(['Identity/Account/Login'], { queryParams: { message: 'Please log in to buy items.' } });
    }
  }

  ngOnInit(): void {
    const itemName = this.route.snapshot.params['name'];

    const observer: Observer<any> = {
      next: (data) => {
        this.item = data;
      },
      error: (error) => {
        console.error(error);
      },
      complete: () => {
        console.log('Item Observer completed');
      }
    };

    this.isAuthenticated = this.authorizeService.isAuthenticated();
    this.item = this.inventoryService.getItemByName(itemName).subscribe(observer);;
  }
}
<div class="item-detail">
  <div class="image-container">
    <img [src]="item.imagePath" alt="{{item.name}} image">
  </div>
  <div class="details-container">
    <h2>{{ item.name }}</h2>
    <p>{{ item.description }}</p>
    <!-- Add other details here -->
    <button ngIf="isAuthenticated | async" (click)="buyItem(item)">Buy Item</button>
  </div>

</div>
